declare class Sema<T = string> {
    private maxConcurrency;
    private freeQ;
    private waitingQ;
    private releaseEmitter;
    private noTokens;
    private pauseFn?;
    private resumeFn?;
    private paused;
    constructor(maxConcurrency: number, { initFn, pauseFn, resumeFn, capacity, }?: {
        initFn?: () => T;
        pauseFn?: () => void;
        resumeFn?: () => void;
        capacity?: number;
    });
    tryAcquire(): T | void;
    acquire(): Promise<T>;
    release(token?: T): void;
    drain(): Promise<T[]>;
    waiting(): number;
}

declare class RateLimit {
    private sema;
    private delay;
    private timeouts;
    constructor(rate: number, { interval, uniformDistribution, }?: {
        interval?: number;
        uniformDistribution?: boolean;
    });
    apply(): Promise<ReturnType<typeof setTimeout>>;
    reset(): number;
}

export { RateLimit, Sema };
